const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div>
      <ListFriends friends={initialFriends} />
    </div>
  );
}

function ListFriends({ friends }) {
  return (
    <div className="sidebar">
      <ul>
        {friends.map((friend) => (
          <Friend
            name={friend.name}
            img={friend.image}
            balance={friend.balance}
            id={friend.id}
          />
        ))}
        <AddFriendForm />
        <SplitBillForm />
      </ul>
    </div>
  );
}

function Friend({ name, img, balance, id }) {
  return (
    <li>
      <h3>{name}</h3>
      <img src={img} alt="friendImg" />
      <p>{balance}</p>
      <button className="button">Select</button>
    </li>
  );
}

function AddFriendForm() {
