      <InputBox>ðŸŒ„ Image URL</InputBox>
      <button className="button">Add</button>
    </form>
  );
}

function InputBox({ children }) {
  return (
    <>
      <label className="preserve-space">{`${children}`.padEnd(20, "  ")}</label>
      <input />
    </>
  );
}
function SplitBillForm() {
  return (
