import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <ListFriends />
    </div>
  );
}

function ListFriends() {
  const [friends, setFriends] = useState([]);
  function handleAddFriend(newFriend) {
    setFriends((friends) => [...friends, newFriend]);
  }
  return (
    <>
      <div className="sidebar">
        <ul>
          {friends.map((friend) => (
            <Friend
              name={friend.name}
              img={friend.image}
              balance={friend.balance}
              id={friend.id}
            />
          ))}
          <AddFriendForm onAddFriend={handleAddFriend} />
        </ul>
      </div>
      <SplitBillForm />
    </>
  );
}

function Friend({ name, img, balance, id }) {
  return (
    <li>
      <h3>{name}</h3>
      <img src={img} alt="friendImg" />
      <p>{balance}</p>
      <button className="button">Select</button>
    </li>
  );
}

function AddFriendForm({ onAddFriend }) {
  const [newName, setNewName] = useState("");
  const [newImg, setNewImg] = useState("");
  function handleSubmit(e) {
    e.preventDefault();
    if (!newName) return;
    const newFriend = { id: newName, newName, newImg, balance: 0 };
    setNewName("");
    setNewImg("");
    onAddFriend(newFriend);
  }
  return (
    <form className="form-add-friend" onSubmit={handleSubmit}>
      <InputBox value={newName} onChange={(e) => setNewName(e.target.value)}>
        👭 Friend name
      </InputBox>
      <InputBox value={newImg} onChange={(e) => setNewImg(e.target.value)}>
        🌄 Image URL
      </InputBox>
      <button className="button">Add</button>
    </form>
  );
}

function InputBox({ children }) {
  return (
    <>
      <label>{`${children}`.padEnd(20, "  ")}</label>
      <input />
    </>
  );
}
function SplitBillForm() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with XXX</h2>
      <InputBox>💰 Bill value</InputBox>
      <InputBox>🧍‍♀️ Your expense</InputBox>
      <InputBox>👭 XXX's expense</InputBox>
      <label>🤑 Who is paying the bill?</label>
      <select>
        <option>XXX</option>
        <option>XXX</option>
      </select>
      <button className="button">Split bill</button>
    </form>
  );
}
