import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];
//improvement2: should include components in App component instead of ListFriends component
export default function App() {
  const [friends, setFriends] = useState(initialFriends);
  // const [isOpen, setOpen] = useState(false);
  const [selectFriend, setSelectFriend] = useState(null);
  const [isOpenAdd, setOpenAdd] = useState(false);
  function handleAddFriend(newFriend) {
    setFriends((friends) => [...friends, newFriend]);
    setOpenAdd(false);
  }
  function handleSelection(id) {
    setSelectFriend(friends?.find((friend) => friend?.id === id));
    // setOpen((isOpen) => (selectFriend?.id === id ? !isOpen : true));
    setOpenAdd(false);

    // setOpen((isOpen) => !isOpen);
  }
  function handleNewBalance(newBalence) {
    setFriends(
      friends.map((friend) =>
        friend.id === selectFriend.id
          ? { ...friend, balance: newBalence }
          : friend
      )
    );
  }
  return (
    <div className="app">
      <div className="sidebar">
        <ListFriends
          friends={friends}
          // onOpen={handleOpen}
          selectFriend={selectFriend}
          // isOpen={isOpen}
        />
        {isOpenAdd && <AddFriendForm onAddFriend={handleAddFriend} />}
        <Button onClick={() => setOpenAdd((isOpenAdd) => !isOpenAdd)}>
          {isOpenAdd ? "Close" : "Add a friend"}
        </Button>
      </div>
      {/* <Button onClick={() => setOpenAdd(!isOpenAdd)}>Add friend</Button> */}
      {selectFriend && (
        <SplitBillForm
          selectFriend={selectFriend}
          onNewBalance={handleNewBalance}
        />
      )}
    </div>
  );
}

function ListFriends({ friends, onOpen, selectFriend, isOpen }) {
  return (
    <ul>
      {friends.map((friend) => (
        <Friend
          friend={friend}
          onOpen={onOpen}
          selectFriend={selectFriend}
          key={friend.id}
          isOpen={isOpen}
        />
      ))}
    </ul>
  );
}

//imporvement1: pass the current object as a prop instead of one by one
function Friend({ friend, onOpen, selectFriend, isOpen }) {
  return (
    <li className={isOpen && selectFriend.id === friend?.id ? "selected" : ""}>
      <h3>{friend.name}</h3>
      <img src={friend.image} alt="friendImg" />

      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you {friend.balance}💰
        </p>
      )}
      {friend.balance < 0 && (
        <p className="red">
          You owe {friend.name} {Math.abs(friend.balance)}💰
        </p>
      )}
      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      {/* <button className="button" onClick={() => onOpen(friend.id)}>
        Select
      </button> */}
      <Button onClick={() => onOpen(friend?.id)}>
        {isOpen && selectFriend.id === friend?.id ? "Close" : "Select"}
      </Button>
    </li>
  );
}
//imporvement5: dynamic select/close button
//imporvement6: dynamic selected bg
//imporvement7: 😡 Cannot read properties of null.Because I set the default state value
//              to null.so when friend.id  is  null.id is NOT gonna work.SO we need to
//              use option chainning here

//use controlled elements
//improvement3:generate unique id by using crypto.randomUUID()
//improvement4:generate random profile photo
function AddFriendForm({ onAddFriend }) {
  const [newName, setNewName] = useState("");
  const [newImg, setNewImg] = useState("https://i.pravatar.cc/48");

  function handleSubmit(e) {
    e.preventDefault();
    if (!newName) return;
    console.log("img", newImg, !newImg);
    const id = crypto.randomUUID();
    const newFriend = {
      id: id,
      name: newName,
      // image: `${newImg ? newImg : "/profile.jpg"}`,
      image: `${newImg}?=${id}`,
      balance: 0,
    };
    console.log("new", newFriend);
    setNewName("");
    setNewImg("https://i.pravatar.cc/48");
    onAddFriend(newFriend);
  }
  function handleNewName(newName) {
    setNewName(newName);
  }
  function handleNewImg(newImg) {
    setNewImg(newImg);
  }
  return (
    <form className="form-add-friend" onSubmit={handleSubmit}>
      <InputBox type="text" value={newName} onNew={handleNewName}>
        👭 Friend name
      </InputBox>
      <InputBox type="text" value={newImg} onNew={handleNewImg}>
        🌄 Image URL
      </InputBox>
      {/* <button className="button">Add</button> */}
      <Button>Add</Button>
    </form>
  );
}

function Button({ onClick, children }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function InputBox({ type, value, onNew, children }) {
  return (
    <>
      <label>{children}</label>
      <input
        type={type}
        value={value}
        disabled={`${!onNew ? "disabled" : ""}`}
        onChange={(e) => {
          onNew(e.target.value);
        }}
      />
    </>
  );
}

function SplitBillForm({ selectFriend, onNewBalance }) {
  const [bill, setBill] = useState("");
  const [yourExpense, setYourExpense] = useState("");
  const [whoPaid, setWho] = useState("you");
  const remain = Number(bill) - Number(yourExpense);
  const newBalence =
    whoPaid === "you"
      ? Number(selectFriend.balance + remain)
      : Number(selectFriend.balance - yourExpense);
  function handleSubmit(e) {
    e.preventDefault();
    if (!bill) return;
    if (!yourExpense) return;
    onNewBalance(newBalence);
    setBill("");
    setYourExpense("");
    setWho("you");
  }
  return (
    <form className="form-split-bill" onSubmit={handleSubmit}>
      <h2>Split a bill with {selectFriend.name}</h2>
      <InputBox type="number" value={bill} onNew={setBill}>
        💰 Bill value
      </InputBox>
      <InputBox type="number" value={yourExpense} onNew={setYourExpense}>
        🧍‍♀️ Your expense
      </InputBox>
      <InputBox type="number" value={remain}>
        👭 {selectFriend.name}'s expense
      </InputBox>
      <label>🤑 Who is paying the bill?</label>
      <select value={whoPaid} onChange={(e) => setWho(e.target.value)}>
        <option value="you">You</option>
        <option value="notYou">{selectFriend.name}</option>
      </select>
      {/* <button className="button">Split bill</button> */}
      <Button>Split bill</Button>
    </form>
  );
}
